create database product;
use product;

CREATE TABLE item(
   id                 INTEGER NOT NULL PRIMARY KEY 
  ,title              VARCHAR(256) NOT NULL
  ,description        VARCHAR(1000) NOT NULL
  ,price              INTEGER NOT NULL
  ,discountPercentage DECIMAL(30,2) NOT NULL
  ,rating             DECIMAL(30,2) NOT NULL
  ,stock              INTEGER  NOT NULL
  ,brand              VARCHAR(256) NOT NULL
  ,category           VARCHAR(45) NOT NULL
  ,thumbnail          VARCHAR(1000) NOT NULL
);


SELECT * FROM item;

use product;

CREATE TABLE images(
   id                 INTEGER  NOT NULL PRIMARY KEY 
  ,image0            VARCHAR(60) NOT NULL
  ,image1            VARCHAR(60)
  ,image2            VARCHAR(60)
  ,image3            VARCHAR(60)
  ,image4            VARCHAR(60)
  ,image5            VARCHAR(60)
);


Select * FROM images;